mapper.insert:
insert into BOOKWRITER.T_PRESCRIPTION (PRESCRIPTION_NO, ORDER_DATETIME, CASE_REC_NO
  )
values (#{prescriptionNo,jdbcType=CHAR}, #{orderDatetime,jdbcType=TIMESTAMP}, #{caseRecNo,jdbcType=CHAR}
  )

mapper.update:
